use super::{hashing::HashableMessage, num_bigint::Operations};
use num_bigint::BigUint;
use std::iter::zip;

/// Compute Phi Schnorr according to specifications
pub fn compute_phi_schnorr((p, _, g): (&BigUint, &BigUint, &BigUint), x: &BigUint) -> BigUint {
    g.mod_exponentiate(x, p)
}

/// Verify Schnorr Proof according to specifications
pub fn verify_schnorr(
    (p, q, g): (&BigUint, &BigUint, &BigUint),
    (e, z): (&BigUint, &BigUint),
    y: &BigUint,
    i_aux: &Vec<String>,
) -> bool {
    let x = compute_phi_schnorr((p, q, g), z);
    let f = HashableMessage::from(vec![p, q, g]);
    // e in Z_q => modulo q
    // x, y in G_q => modulo p
    let c_prime = x.mod_multiply(&y.mod_exponentiate(e, p).mod_inverse(p), p);
    let mut l: Vec<HashableMessage> = vec![];
    l.push(HashableMessage::from("SchnorrProof"));
    if !i_aux.is_empty() {
        l.push(HashableMessage::from(i_aux));
    }
    let h_aux = HashableMessage::Composite(l);
    let l_final: Vec<HashableMessage> = vec![
        f,
        HashableMessage::from(y),
        HashableMessage::from(&c_prime),
        h_aux,
    ];
    let e_prime = HashableMessage::from(&l_final)
        .recursive_hash()
        .into_biguint();
    &e_prime == e
}

/// Compute phi exponation according to specifications
pub fn compute_phi_exponentiation(
    (p, _, _): (&BigUint, &BigUint, &BigUint),
    x: &BigUint,
    gs: &[BigUint],
) -> Vec<BigUint> {
    gs.iter().map(|g| g.mod_exponentiate(x, p)).collect()
}

/// Verify Exponation proof according to specifications
pub fn verify_exponentiation(
    (p, q, g): (&BigUint, &BigUint, &BigUint),
    gs: &Vec<BigUint>,
    ys: &Vec<BigUint>,
    (e, z): (&BigUint, &BigUint),
    i_aux: &Vec<String>,
) -> bool {
    let xs = compute_phi_exponentiation((p, q, g), z, gs);
    let f_list = vec![
        HashableMessage::from(p),
        HashableMessage::from(q),
        HashableMessage::from(gs),
    ];
    let f = HashableMessage::from(f_list);
    let c_prime_s: Vec<BigUint> = zip(&xs, ys)
        .map(|(x, y)| x.mod_multiply(&y.mod_exponentiate(e, p).mod_inverse(p), p))
        .collect();
    let mut l: Vec<HashableMessage> = vec![];
    l.push(HashableMessage::from("ExponentiationProof"));
    if !i_aux.is_empty() {
        l.push(HashableMessage::from(i_aux));
    }
    let h_aux = HashableMessage::from(&l);
    let l_final: Vec<HashableMessage> = vec![
        f,
        HashableMessage::from(ys),
        HashableMessage::from(&c_prime_s),
        h_aux,
    ];
    let e_prime = HashableMessage::from(&l_final)
        .recursive_hash()
        .into_biguint();
    &e_prime == e
}

