    #[test]
    fn test_verify_schnorr_2048() {
        let p= BigUint::from_hexa_string("").unwrap();
        let q=  BigUint::from_hexa_string("").unwrap();
        let g = BigUint::from_hexa_string("0x4").unwrap();
        let e = BigUint::from_hexa_string(
            "0x29D551590009AC768A674AC0C42416FDDCF261DE93CA78ED53632929169406E1",
        )
        .unwrap();
        let z = BigUint::from_hexa_string("").unwrap();
        let y = BigUint::from_hexa_string("").unwrap();
        let add_info: Vec<String> = vec!["test-0".to_string()];
        assert!(verify_schnorr((&p, &q, &g), (&e, &z), &y, &add_info))
    }

